import React, { useEffect, useState, useRef, useCallback } from 'react';
import ArticleCard from '../components/ArticleCard';
import LoadingSpinner from '../components/LoadingSpinner';

const NewsFeed = () => {
  const [articles, setArticles] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true); // New state to check if there are more articles to load
  const observer = useRef();

  const fetchArticles = useCallback(async () => {
    setLoading(true);
    const response = await fetch(
      `https://newsapi.org/v2/top-headlines?country=us&pageSize=10&page=${page}&apiKey=${process.env.REACT_APP_NEWS_API_KEY}`
    );
    const data = await response.json();
    setArticles((prevArticles) => [...prevArticles, ...data.articles]);
    setHasMore(data.articles.length > 0); // Set hasMore to false if no articles are returned
    setLoading(false);
  }, [page]);

  useEffect(() => {
    fetchArticles();
  }, [fetchArticles]);

  const lastArticleElementRef = useCallback(
    (node) => {
      if (loading) return;
      if (observer.current) observer.current.disconnect();
      observer.current = new IntersectionObserver((entries) => {
        if (entries[0].isIntersecting && hasMore) {
          setPage((prevPage) => prevPage + 1);
        }
      });
      if (node) observer.current.observe(node);
    },
    [loading, hasMore]
  );

  return (
    <div className="news-feed">
      {articles.map((article, index) => {
        if (articles.length === index + 1) {
          return (
            <ArticleCard
              ref={lastArticleElementRef}
              key={article.title}
              title={article.title}
              description={article.description}
            />
          );
        } else {
          return (
            <ArticleCard
              key={article.title}
              title={article.title}
              description={article.description}
            />
          );
        }
      })}
      {loading && <LoadingSpinner />}
    </div>
  );
};

export default NewsFeed;
